window.addEventListener('DOMContentLoaded', () => { const changeColorIcon = (query) => { const icons = document.querySelectorAll(query); icons.forEach((icon) => { const url = icon.dataset.src.split('?')[0]; const color = icon.dataset.src.split('?')[1].split('=')[1]; icon.style.backgroundColor = color; icon.style.webkitMask = `url(${url}) no-repeat center`; }) } })